import { Component } from '@angular/core';
import { MyServiceService } from './my-service.service';

@Component({
  selector: 'educative-app',
  templateUrl: './app.component.html',
  styleUrls: []
})
export class AppComponent {
  app_title:string; 
  heading_name:string;
  hero_body_text:string;
  register_service_text:string;

  constructor(private myService:MyServiceService){
    this.app_title = 'educative_app';
    this.heading_name = this.myService.heading_name;
    this.hero_body_text = this.myService.hero_body_text;
    this.register_service_text = this.myService.register_service_text;
  }
}
<!DOCTYPE html>
<HTML>
  <head>
    <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bulma@0.9.2/css/bulma.min.css">
    <style>
      body{
        background-color:gainsboro
      }
      .center_content{
        position:absolute;
        top:50%;
        transform:translate(0,-50%);
        width:70%;
        height:100%;
        margin:auto;
        text-align: center;  
      }
      .app_main_heading{
        color:black;
        font-weight: bold;
        font-size: xx-large;
        font-family: Arial, Helvetica, sans-serif;
      }
      .app_sub_heading{
        color:blue;
        font-weight: 700;
        font-size:larger;
        font-family:Verdana, Geneva, Tahoma, sans-serif;
      }
    </style>  
  </head>
  <body>
    <div class="center_content">
      <img src="./assets/images/educative_logo.png">
          <p class = "app_main_heading">
            {{ hero_body_text }}
          </p>
          <p class = "app_sub_heading">
            {{ register_service_text}}
          </p>
    </div>
  </body>
</HTML>
<router-outlet></router-outlet>
