import { Component } from '@angular/core';
import { MyServiceService } from './my-service.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title:string; 
  heading_name:string;
  hero_body_text:string;

  constructor(private myService:MyServiceService){
    this.title= 'educative_app';
    this.heading_name = this.myService.heading_name;
    this.hero_body_text = this.myService.hero_body_text;
  }
}
<!DOCTYPE html>
<HTML>
  <head>
    <meta name=”viewport” content=”width=device-width, initial-scale=1.0”>
    <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bulma@0.9.2/css/bulma.min.css">
    <style>
      body{
        margin:0;
        padding:0;
      }
      p{
        text-align: center;
      }
      .text-color{
        color:blueviolet
      }
      .navbar{
        background-color: #d0d5e1;
        border: 1px solid;
        border-color: black;

      }
    </style>  
  </head>
  <body>
    <nav class="navbar">
      <div class="container">
          <div class="navbar-start">
            <a class="navbar-item" href="app.component.html">
              <img src="./assets/images/Angular.png" alt="Home Page">
               <div style="width:10px; height:50px;margin-right:10px;"></div>
               {{ heading_name }}
            </a>
        </div>
      </div>
    </nav>

    <section class="hero is-link is-fullheight-with-navbar">
      <div class="hero-body" style="background-color: white;">
        <p class="title" style="color:black;">
          <img src="./assets/images/educative_logo.png">
           {{ hero_body_text }}
        </p>
      </div>
    </section>
  </body>
</HTML>
<router-outlet></router-outlet>
